package bitwise.and;

/**
 * @author Ritam
 *
 */
public class AndDemo {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int a =-9;
       int b =10;
       System.out.println(" a&b "+(a&b));
       System.out.println(" b&a "+(b&a));
       
       System.out.println(" a&1 "+(a&1)); // odd
       System.out.println(" b&1 "+(b&1));  //even
       
       
       
       
	}

}
